mapper.select:
select enterpriseno 
	from tb_enterprises 
	where enterprisebizno = #{enterprisebizno}

select count(*) 
	from tb_enterprises 
	where enterprisebizno = #{enterprisebizno}

mapper.insert:
 insert
  	into
  	tb_enterprises(
  		enterprisebizno
  		, enterpriseat
  		, userno
  		, userat
  		, activeat
  		, representat
  		, enterprisename
  		, enterpriseemail
		, registerdate  		
  	)
 	values(
 		#{enterprisebizno}
 		, #{enterpriseat}
 		, #{userno}
		, #{userat}	 
	   	, #{activeat}
	   	, #{representat}
	   	, #{enterprisename}
	   	, #{enterpriseemail}
	   	, now()
 	)
